function extractUniqueId(data) {
    // Safely access adozo.adoszam and munkavallalo.adoazonosito
    const companyTaxNumber = data?.adozo?.adoszam;
    const employeeTaxNumber = data?.munkavallalo?.adoazonosito;

    // Check if both values exist
    if (companyTaxNumber && employeeTaxNumber) {
        return companyTaxNumber + "-" + employeeTaxNumber;
    } else if (companyTaxNumber) {
      return companyTaxNumber;
    } else {
        return null; // Return null or another default value if any part is missing
    }
}

function normalizeFormFields(fieldArray) {
