function getDate(filename) {
    // Find the position of the last underscore
    const lastUnderscoreIndex = filename.lastIndexOf('_');

    // Find the position of the .xml extension
    const extensionIndex = filename.lastIndexOf('.xml');

    // Extract the string between the last underscore and the .xml extension
    const extractedString = filename.slice(lastUnderscoreIndex + 1, extensionIndex);

    const [year, month, day] = extractedString.split('.').map(Number);

    // Create a new Date object (note: month is 0-indexed in JavaScript)
    const date = new Date(year, month - 1, day);

    return date;
}

