function isNumber(value) {
  return !isNaN(parseFloat(value));
}

// take a single field from source and target and see if they are the same
const diffField = (source, target) => (field) => {
  const sValue = source && source[field]?source[field]:'';
  const tValue = target && target[field]?target[field]:'';

  return {
    field: field,
    equals: sValue === tValue,
    value: sValue,
    newValue: tValue,
    diff: isNumber(sValue) && isNumber(tValue) ? parseFloat(tValue) - parseFloat(sValue): 0 
  }  
}

const diffForm = (mapping) => (form) => {

  const vFields = form.value?.mezok?.mezo;
  const cFields = form.changedTo?.mezok?.mezo;

  const vKeys = (vFields)?Object.keys(vFields):[];
  const cKeys = (cFields)?Object.keys(cFields):[];

  const keys = [...new Set([...vKeys, ...cKeys])];

  const comparator = diffField(vFields, cFields);

  const result = keys.map(comparator).reduce((acc,obj) => {
    const name = mapping[obj.field];

    if (!obj.equals) {
      acc[obj.field] = {
        equals: obj.equals,
        value: obj.value,
        newValue: obj.newValue,
        diff: obj.diff,
        name: name?.Adoazon
      };      
    }  

    return acc;
  }, {});

  form.diff = result;

  return form;  
}

